import React from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

const DraggableComponent = ({ name }) => {
  const [{ isDragging }, drag] = useDrag({
    type: "component", // Define the type property
    item: { type: "component", name },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: "move",
        border: "1px solid #ccc",
        padding: "10px",
        marginBottom: "5px",
      }}
    >
      {name}
    </div>
  );
};

const CanvasArea = ({ components, onMoveComponent }) => {
  const [, drop] = useDrop({
    accept: "component",
    drop: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const left = Math.round(monitor.getClientOffset().x - delta.x);
      const top = Math.round(monitor.getClientOffset().y - delta.y);
      onMoveComponent(item.name, left, top);
    },
  });

  return (
    <div
      ref={drop}
      style={{
        width: "500px",
        height: "500px",
        border: "1px solid #ccc",
        position: "relative",
      }}
    >
      {components.map((component) => (
        <div
          key={component.name}
          style={{
            position: "absolute",
            left: component.left,
            top: component.top,
            cursor: "move",
          }}
        >
          {component.name === "Text Input" && <p>text</p>}
        </div>
      ))}
    </div>
  );
};

const FormBuilder = () => {
  const [formLabel, setFormLabel] = React.useState("");
  const [components, setComponents] = React.useState([]);

  const handleMoveComponent = (name, left, top) => {
    setComponents((prevState) => prevState.concat({ name, left, top }));
  };

  const handleSaveForm = () => {
    const formData = {
      label: formLabel,
      components: components,
    };
    console.log(JSON.stringify(formData)); // You can replace console.log with your save logic
  };

  return (
    <div>
      <h1>Form Builder</h1>
      <div>
        <label htmlFor="formLabel">Form Label:</label>
        <input
          type="text"
          id="formLabel"
          value={formLabel}
          onChange={(e) => setFormLabel(e.target.value)}
        />
      </div>
      <div style={{ display: "flex" }}>
        <div style={{ marginRight: "20px" }}>
          <h2>Available Components</h2>
          <DraggableComponent name="Date Input" />
          <DraggableComponent name="Text Input" />
        </div>
        <div>
          <h2>Canvas Area</h2>
          <CanvasArea
            components={components}
            onMoveComponent={handleMoveComponent}
          />
          <button onClick={handleSaveForm}>Save Form</button>
        </div>
      </div>
    </div>
  );
};

const AppWithDndProvider = () => {
  return (
    <DndProvider backend={HTML5Backend}>
      <FormBuilder />
    </DndProvider>
  );
};

export default AppWithDndProvider;
