import React, { useState } from "react";
import { useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

// Define draggable form components
const DraggableComponent = ({ name, type, onDrop }) => {
  const [{ isDragging }, drag] = useDrag({
    type,
    item: { type, name },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: "move",
        border: "1px solid #ccc",
        padding: "10px",
        marginBottom: "5px",
      }}
      onClick={() => onDrop({ type, name })}
    >
      {name}
    </div>
  );
};

// Define the canvas area where components can be dropped
const CanvasArea = ({ components, onMoveComponent }) => {
  const [, drop] = useDrop({
    accept: "component",
    drop: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const left = Math.round(item.left + delta.x);
      const top = Math.round(item.top + delta.y);
      onMoveComponent(item.id, left, top);
    },
  });

  return (
    <div
      ref={drop}
      style={{
        width: "500px",
        height: "500px",
        border: "1px solid #ccc",
        position: "relative",
      }}
    >
      {components.map((component) => (
        <div
          key={component.id}
          style={{
            position: "absolute",
            left: component.left,
            top: component.top,
          }}
        >
          {component.name}
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const [components, setComponents] = useState([]);
  const [formData, setFormData] = useState({ label: "", form: [] });

  const handleDropComponent = ({ type, name }) => {
    const newComponent = {
      id: Math.random().toString(36).substr(2, 9),
      name,
      left: 0,
      top: 0,
    };
    setComponents((prevState) => [...prevState, newComponent]);
  };

  const handleMoveComponent = (id, left, top) => {
    setComponents((prevState) =>
      prevState.map((component) =>
        component.id === id ? { ...component, left, top } : component
      )
    );
  };

  const handleSaveForm = () => {
    setFormData({
      label: prompt("Enter form label:"),
      form: components.map(({ id, name, left, top }) => ({
        id,
        name,
        left,
        top,
      })),
    });
  };

  return (
    <div>
      <h1>Form Builder</h1>
      <div style={{ display: "flex" }}>
        <div style={{ marginRight: "20px" }}>
          <h2>Available Components</h2>
          <DraggableComponent
            name="Date Input"
            type="component"
            onDrop={handleDropComponent}
          />
          <DraggableComponent
            name="Text Input"
            type="component"
            onDrop={handleDropComponent}
          />
        </div>
        <div>
          <h2>Canvas Area</h2>
          <CanvasArea
            components={components}
            onMoveComponent={handleMoveComponent}
          />
          <button onClick={handleSaveForm}>Save Form</button>
        </div>
      </div>
      <pre>{JSON.stringify(formData, null, 2)}</pre>
    </div>
  );
};

export default App;
