import React from "react";
import { useDrag, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

// Define a draggable form component
const DraggableComponent = ({ name, type }) => {
  const [{ isDragging }, drag] = useDrag({
    item: { type },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: "move",
        border: "1px solid #ccc",
        padding: "10px",
        marginBottom: "5px",
      }}
    >
      {name}
    </div>
  );
};

// Define the canvas area where components can be dropped
const CanvasArea = () => {
  return (
    <div
      style={{
        width: "500px",
        height: "500px",
        border: "1px solid #ccc",
        position: "relative",
      }}
    >
      {/* You can place dropped components here */}
    </div>
  );
};

const App = () => {
  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <h1>Drag and Drop Form Builder</h1>
        {/* Render draggable components */}
        <DraggableComponent name="Text Field" type="text-field" />
        <DraggableComponent name="Checkbox" type="checkbox" />
        <DraggableComponent name="Radio Button" type="radio" />
        {/* Render the canvas area */}
        <CanvasArea />
      </div>
    </DndProvider>
  );
};

export default App;
